text.content:
model Book {
  id          Int    @id @default(autoincrement())
  title       String
  description String
  price       Int
  authorId    Int
  author      Author @relation(fields: [authorId], references: [id])
}

model Author {
  id    Int    @id @default(autoincrement())
  name  String
  books Book[]
}

